mal_inline_link = element link {
  attribute style { xsd:NMTOKENS } ?,
  attribute * - (mal:* | local:*) { text } *,

  ( ( attribute xref { text },
      mal_inline ?
    ) |
    ( attribute dref { text },
      attribute href { text } ?,
      mal_inline
    ) |
    ( attribute href { text },
      mal_inline
    )
  )
}
